// Helper functions for conversation memory
async function getRecentConversations(chatId, limit = 10) {
  if (!supabase) {
    return [];
  }

  try {
    const { data, error } = await supabase
      .from('conversation_history')
      .select('*')
      .eq('chat_id', chatId)
      .order('created_at', { ascending: false })
      .limit(limit);

    if (error) {
      console.error('[MEMORY] –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∏—Å—Ç–æ—Ä–∏–∏:', error);
      return [];
    }

    return data.reverse(); // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –≤ —Ö—Ä–æ–Ω–æ–ª–æ–≥–∏—á–µ—Å–∫–æ–º –ø–æ—Ä—è–¥–∫–µ
  } catch (err) {
    console.error('[MEMORY] –ö—Ä–∏—Ç–∏—á–µ—Å–∫–∞—è –æ—à–∏–±–∫–∞:', err);
    return [];
  }
}

async function saveConversation(chatId, userId, username, firstName, messageType, userMessage, botResponse) {
  if (!supabase) {
    return;
  }

  try {
    const { error } = await supabase
      .from('conversation_history')
      .insert({
        chat_id: chatId,
        user_id: userId,
        username: username || '',
        first_name: firstName || '',
        message_type: messageType,
        user_message: userMessage,
        bot_response: botResponse
      });

    if (error) {
      console.error('[MEMORY] –û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è:', error);
    } else {
      console.log('[MEMORY] ‚úÖ –°–æ—Ö—Ä–∞–Ω–µ–Ω–æ –≤ –±–∞–∑—É');
    }
  } catch (err) {
    console.error('[MEMORY] –ö—Ä–∏—Ç–∏—á–µ—Å–∫–∞—è –æ—à–∏–±–∫–∞:', err);
  }
}

